actions.action:
printf "{@name=Name}: " > /data/local/stat.txt;
echo "%tw_filename1%" >> /data/local/stat.txt;
printf "{@name=Name} ({@real=real}): " >> /data/local/stat.txt;
realpath "%tw_filename1%" >> /data/local/stat.txt;
printf "{@part_size=Size}: " >> /data/local/stat.txt;
du -h "%tw_filename1%" | sed 's*%tw_filename1%**g' >> /data/local/stat.txt;
stat "%tw_filename1%" | grep Access | sed 's/Access/{@access=Access}/g' 2>/dev/null 1>> /data/local/stat.txt;
stat "%tw_filename1%" | grep Modify | sed 's/Modify/{@time_m=Modify}/g' 2>/dev/null 1>> /data/local/stat.txt;
stat "%tw_filename1%" | grep Change | sed 's/Change/{@time_c=Change}/g' 2>/dev/null 1>> /data/local/stat.txt;

echo "MD5:" > /data/local/stat.txt;
md5sum "%tw_filename1%" | sed 's*%tw_filename1%**g' >> /data/local/stat.txt;
echo "SHA1:" >> /data/local/stat.txt;
sha1sum "%tw_filename1%" | sed 's*%tw_filename1%**g' >> /data/local/stat.txt;
echo "SHA3:" >> /data/local/stat.txt;
sha3sum "%tw_filename1%" | sed 's*%tw_filename1%**g' >> /data/local/stat.txt;
echo "SHA256:" >> /data/local/stat.txt;
sha256sum "%tw_filename1%" | sed 's*%tw_filename1%**g' >> /data/local/stat.txt;
echo "SHA512:" >> /data/local/stat.txt;
sha512sum "%tw_filename1%" | sed 's*%tw_filename1%**g' >> /data/local/stat.txt;
echo "SUM:" >> /data/local/stat.txt;
sum "%tw_filename1%" >> /data/local/stat.txt;

action.action:
cd "%tw_theme_path%";

if [ ! -f "./zip" ]; then unzip -p ./ui.zip zip > ./zip; fi;
if [ ! -x "./zip" ]; then chmod 777 ./zip; fi;

cd "%tw_file_location1%";

"%tw_theme_path%/zip" -%tw_pack_lvl% -r "%tw_file_location1%.zip" *;
